function setActiveTab(tabId) {
  document.getElementById(tabId).classList.add("active");
}

function removeActiveTab(tabId) {
  document.getElementById(tabId).classList.remove("active");
}

function toggleContentVisibility(showClass, hideClass) {
  document.getElementsByClassName(showClass)[0].classList.remove("d-none");
  document.getElementsByClassName(hideClass)[0].classList.add("d-none");
}

document.getElementById("お知らせ").addEventListener("click", () => {
  setActiveTab("お知らせ");
  removeActiveTab("コラム");
  toggleContentVisibility("notice", "column");
  toggleContentVisibility("notices-list", "columns-list");
});

document.getElementById("コラム").addEventListener("click", () => {
  setActiveTab("コラム");
  removeActiveTab("お知らせ");
  toggleContentVisibility("column", "notice");
  toggleContentVisibility("columns-list", "notices-list");
});
